    public function __contruct($connectionName = false)
    {
        if (!$connectionName) {
            $connectionName = "doctrine_site" ;
        }
        Doctrine_Manager::getInstance()->bindComponent($connectionName, $connectionName);
    }
    #################################################
    ########### REFACTORED CODE #####################
