    public static function getPageLogo($logoId)
    {
        $pageLogo = Doctrine_Query::create()->select()
        ->from('logo l')
        ->where("l.id = ?", $logoId)
        ->fetchArray();
        return  !empty($pageLogo[0]) ? $pageLogo[0] : '';
    }
