        ->where('o.deleted=' . "'$flag'")
        ->andWhere("s.name LIKE ?", "$keyword%")
        ->andWhere('o.userGenerated=0')
        ->orderBy("s.id ASC")->groupBy('s.name')->limit(5)->fetchArray();
        //print_r($data); die;
