        $this->title = BackEnd_Helper_viewHelper::stripSlashesFromString($params['addofferTitle']);

        if (isset($params['deepLinkStatus'])) {
            $this->refURL =  BackEnd_Helper_viewHelper::stripSlashesFromString($params['offerRefUrl']);
