class Widget extends BaseWidget
{
    public static function getAllWidgets()
    {
        $widgetsList = Doctrine_Query::create()->select()
            ->from('widget w')
            ->where('w.deleted=0')
            ->fetchArray();
        return $widgetsList;
    }

    public static function addFunctionNames()
    {
        $widgets = self::getAllWidgets();
        foreach ($widgets as $widget) {
            switch (trim($widget['slug'])) {
                case 'popular_category':
                    self::addFunction($widget['id'], 'popularCategoryWidget');
                    break;
                case 'e_mail_subscription':
                    self::addFunction($widget['id'], 'popularCategoryWidget');
                    break;
                case 'popular_stores':
                    self::addFunction($widget['id'], 'popularShopWidget');
                    break;
                case 'stuur_een':
                    self::addFunction($widget['id'], 'socialCodeWidget');
                    break;
                case 'popular_editor':
                    self::addFunction($widget['id'], 'popularEditorWidget');
                    break;
                case 'most _popular_fashion':
                    self::addFunction($widget['id'], 'plusRecentlyAddedArticles');
                    break;
                default:
                    break;
            }
        }
        self::addNewDefaultWidgets('Popular Offer For Plus', 'plusTopPopularOffers');
        self::addNewDefaultWidgets('Shop Also Viwes', 'shopsAlsoViewedWidget');
        self::addNewDefaultWidgets('Popular Page Editor', 'getPageEditorWidget');
    }

    public static function addNewDefaultWidgets($widgetTitle, $functionName)
    {
        $widget = new Widget();
        $widget->title = $widgetTitle;
        $widget->function_name = $functionName;
        $widget->status = 1;
        $widget->showWithDefault = 0;
        $widget->save();
    }

    public static function addFunction($widgetId, $functionName)
    {
        Doctrine_Query::create()
            ->update('Widget w')
            ->set('w.function_name', '"'.$functionName.'"')
            ->where('id='.$widgetId)
            ->execute();
        return true;
    }

    /**
     * save widget in database
     * @param array $params
     */
    public function addWidget($params)
    {
        $w = new Widget();
        $w->title = BackEnd_Helper_viewHelper::stripSlashesFromString($params ['title']);
        $w->content = BackEnd_Helper_viewHelper::stripSlashesFromString($params ['content']);
        $w->save ();
        //call cache function
        FrontEnd_Helper_viewHelper::clearCacheByKeyOrAll('all_widget_list');
        return $w->id;
    }

    /**
     * get widget list from database for
     * @param array $params
     */

     function getDefaultwidgetList()
     {
        $widgetsList = Doctrine_Query::create()->select()
                    ->from('widget w')
                    ->where('w.userDefined=0')
                    ->andWhere('w.deleted=0')
                    ->andWhere('status = 1')
                    ->fetchArray();
        return $widgetsList;
     }

     function getUserDefinedWidgetList()
     {
        $widgetsList = Doctrine_Query::create()->select()
        ->from('widget w')
        ->where('w.deleted=0')
        ->andWhere('w.status=1')
        ->orderBy('title')
        ->fetchArray();
        return $widgetsList;
     }




/**
 * changeStatus changes the status to online and offline
 * @param $params
 * @author mkaur
 */
    public static function changeStatus($params)
    {
        $status = new Widget();
        if ($params['state'] == 'online') {
            $status = Doctrine_Query::create ()
            ->update ('Widget')
            ->set ('status','1')
            ->where ('id='.$params['id'])
            ->execute();
            return $params['id'];
        }
        if ($params['state'] == 'offline') {
            $status = Doctrine_Query::create ()
            ->update('Widget')
            ->set('status', '0')
            ->where('id='.$params['id'])
            ->execute();
            return $params['id'];
        } else {
            $id = null;
        }
        //call cache function
        FrontEnd_Helper_viewHelper::clearCacheByKeyOrAll('all_widget_list');
    }
/**
 * @return Doctrine_Query, Doctrine_Query_Abstracts
 * @author mkaur updated by blal
 */
    public static function getWidgetList($params)
    {
        $srh = @$params["searchText"] != 'undefined' ? @$params["searchText"] : '';
        //$srh = isset($params["searchText"]) ? $params["searchText"] : '';
        $data = Doctrine_Query::create ()
        ->select ('w.*')
        ->from ("Widget w")
        ->Where("deleted = 0" )
        ->andWhere("w.title LIKE ?", "$srh%")
        ->orderBy("w.id DESC");

        $list = DataTable_Helper::generateDataTableResponse($data,
                $params, array("__identifier" => 'w.id', 'w.id','w.title','w.content'),
                array(), array());

        return $list;
    }
/**
 * Search top five shops and shows in autocomplete
 * @param string $flag
 * @author mkaur
 * @version 1.0
 */
    public static function searchKeyword($keyword)
    {
        $data = Doctrine_Query::create()->select('w.title as title')
        ->from("Widget w")
        ->where("w.title LIKE ?", "$keyword%")
        ->andWhere("w.status=1")
        ->orderBy("w.title ASC")
        ->limit(5)->fetchArray();
        return $data;
    }
    public static function updateWidget($id)
    {
        $data = Doctrine_Query::create()->select('w.*')
        ->from("Widget w")
        ->where("w.id=".$id)
        ->fetchOne(null, Doctrine::HYDRATE_ARRAY);
        //echo $data->getSqlQuery(); die;
        //call cache function
        FrontEnd_Helper_viewHelper::clearCacheByKeyOrAll('all_widget_list');
        return $data;

    }

    public function editWidgetRecord($params)
    {
        $content = addslashes($params['content']);
        $data = Doctrine_Query::create()
        ->update('widget w')
        ->set('w.title',"'". BackEnd_Helper_viewHelper::stripSlashesFromString($params['title']) ."'")
        ->set('w.content',"'". BackEnd_Helper_viewHelper::stripSlashesFromString($content) ."'")
        ->where('w.id='.$params['id']);
        $data->execute();
        //echo $data->getSqlQuery(); die();
        //call cache function
        FrontEnd_Helper_viewHelper::clearCacheByKeyOrAll('all_widget_list');
        return true;
    }
